import{_ as a}from"./plugin-vue_export-helper-c27b6911.js";import{r as n,o as s,c,a as e,d as l,w as r,b as i,e as o}from"./app-9673d752.js";const u={},p=e("h1",{id:"customer-experience",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#customer-experience","aria-hidden":"true"},"#"),i(" Customer Experience")],-1),d={class:"table-of-contents"},h=o('<p>客户体验是消费者在消费过程的各个阶段（包括购买前、消费和购买后阶段「pre-purchase, consumption, and post-purchase」）的认知「cognitive」、情感「affective」、感官「sensory」和行为「behavioral」反应的总和。</p><h2 id="principle-of-cx-design" tabindex="-1"><a class="header-anchor" href="#principle-of-cx-design" aria-hidden="true">#</a> Principle of CX Design</h2><figure><img src="https://pic.hanjiaming.com.cn/2023/12/09/a4cf677147672.png" alt="1702054769554.png" tabindex="0" loading="lazy"><figcaption>1702054769554.png</figcaption></figure><p><strong>CX Design Process</strong></p><ul><li>Discover</li><li>Describe</li><li>Determine</li><li>Develop</li><li>Deliver</li></ul><div class="hint-container info"><p class="hint-container-title">Customer Experience Management vs. CRM</p><ul><li><strong>客户关系管理（CRM）</strong><ul><li>是一个用于管理公司与现有和潜在客户的所有互动的系统。</li><li>例如，当一个客户购买了一家公司的产品，这家公司可能会使用CRM系统来记录这个客户的购买历史，以便在未来提供个性化的服务或产品推荐。</li></ul></li><li><strong>客户体验管理（CEM）</strong><ul><li>则是一种策略，用于跟踪、管理和优化客户与品牌的所有互动。</li><li>例如，一家酒店可能会使用CEM策略来确保从预订，到入住，再到退房的整个过程都为客户提供了一致的、高质量的体验。</li></ul></li></ul></div><h2 id="why-cx-matters" tabindex="-1"><a class="header-anchor" href="#why-cx-matters" aria-hidden="true">#</a> Why CX Matters</h2><p>积极的客户体验对于您的业务成功至关重要，因为满意的客户很可能成为忠诚的客户，可以帮助您增加收入</p><div class="hint-container tip"><p class="hint-container-title">important factors for a successful customer experience strategy</p><ul><li>竞争洞察</li><li>消费者研究</li><li>市场数据</li><li>使命与愿景</li></ul></div><h2 id="customer-experience-strategies" tabindex="-1"><a class="header-anchor" href="#customer-experience-strategies" aria-hidden="true">#</a> Customer Experience Strategies</h2><h3 id="如何理解客户体验" tabindex="-1"><a class="header-anchor" href="#如何理解客户体验" aria-hidden="true">#</a> 如何理解客户体验</h3><ul><li>Mystery shopping</li><li>Experience mapping</li><li>Process mapping</li><li>Customer activity cycle</li><li>Ethnographic methods</li><li>Participant observation</li><li>Non-participant observation</li></ul><h3 id="measure" tabindex="-1"><a class="header-anchor" href="#measure" aria-hidden="true">#</a> Measure</h3><p>Net Promoter Score® (NPS) 净推荐值显示了愿意向朋友、家人或同事推荐您的客户的百分比。...</p><ul><li>客户满意度 (CSAT)</li><li>客户流失率 (CCR)</li><li>客户努力得分 (CES)</li></ul><h3 id="how-to-create-customer-experience-clues" tabindex="-1"><a class="header-anchor" href="#how-to-create-customer-experience-clues" aria-hidden="true">#</a> How to create customer experience clues</h3><ul><li>communications</li><li>visual identity</li><li>product presence</li><li>co-branding</li><li>spatial environments</li><li>websites and electronic media</li><li>people</li></ul>',17);function m(f,g){const t=n("router-link");return s(),c("div",null,[p,e("nav",d,[e("ul",null,[e("li",null,[l(t,{to:"#principle-of-cx-design"},{default:r(()=>[i("Principle of CX Design")]),_:1})]),e("li",null,[l(t,{to:"#why-cx-matters"},{default:r(()=>[i("Why CX Matters")]),_:1})]),e("li",null,[l(t,{to:"#customer-experience-strategies"},{default:r(()=>[i("Customer Experience Strategies")]),_:1}),e("ul",null,[e("li",null,[l(t,{to:"#如何理解客户体验"},{default:r(()=>[i("如何理解客户体验")]),_:1})]),e("li",null,[l(t,{to:"#measure"},{default:r(()=>[i("Measure")]),_:1})]),e("li",null,[l(t,{to:"#how-to-create-customer-experience-clues"},{default:r(()=>[i("How to create customer experience clues")]),_:1})])])])])]),h])}const C=a(u,[["render",m],["__file","ce.html.vue"]]);export{C as default};
