import{_ as e}from"./plugin-vue_export-helper-c27b6911.js";import{o as i,c as a,e as l}from"./app-26e0c976.js";const o={},r=l('<h1 id="docker" tabindex="-1"><a class="header-anchor" href="#docker" aria-hidden="true">#</a> Docker</h1><ul><li>Docker 是一种提供虚拟化解决方案的技术，允许开发人员将应用程序及其所有依赖项打包到标准化单元中进行部署。</li><li>与虚拟机相比，相对轻量级的虚拟化</li><li>应用程序在容器内运行，彼此隔离</li><li>一个容器可以在几秒钟内启动</li><li>机器上更有效地利用计算资源</li></ul><h2 id="basic-concepts" tabindex="-1"><a class="header-anchor" href="#basic-concepts" aria-hidden="true">#</a> Basic Concepts</h2><ul><li><strong>Image</strong>: a read-only file containing the application</li><li><strong>Container</strong>: an instance of an image for executing the application</li><li>Repository: where images are stored</li></ul><h2 id="benefits-of-docker" tabindex="-1"><a class="header-anchor" href="#benefits-of-docker" aria-hidden="true">#</a> Benefits of Docker</h2><ol><li>缓存容器集群。</li><li>灵活资源共享。</li><li>可扩展性 - 许多容器可以放置在单个主机上。</li><li>在比标准服务器便宜得多的硬件上运行您的服务。</li><li>快速部署，创建新实例简单，迁移更快。</li><li>移动和维护应用程序简单。</li><li>更好的安全性，与容器内运行的代码协作所需的访问权限更少，软件依赖性更少。</li></ol>',6),t=[r];function c(n,s){return i(),a("div",null,t)}const f=e(o,[["render",c],["__file","docker.html.vue"]]);export{f as default};
