import{_ as o}from"./plugin-vue_export-helper-c27b6911.js";import{r as t,o as n,c as r,a as i,b as e,d as l,f as s,e as c}from"./app-83c15424.js";const d={},p=i("h1",{id:"introduction",tabindex:"-1"},[i("a",{class:"header-anchor",href:"#introduction","aria-hidden":"true"},"#"),e(" Introduction")],-1),h={href:"https://www.coursera.org/learn/probabilistic-graphical-models",target:"_blank",rel:"noopener noreferrer"},u={href:"https://mitpress.mit.edu/books/probabilistic-graphical-models",target:"_blank",rel:"noopener noreferrer"},g=i("p",null,"概率模型是机器学习中的重要模型。没有凭空现象，我更倾向于这是一种思想理念，能够为解决一类问题提供一种可行的模型。为了建立这种模型，我们需要深入理解这个模型的原理，以及如何建立。",-1),m=i("p",null,"概率模型是比较传统，或者说相对古老的机器学习模型。尽管现在像深度学习和神经网络更受欢迎，概率模型依然是学者不可忽视的重要模型。它常用于做推断和决策制定。",-1),b=i("p",null,"本节也只是记录了我的笔记。如遇全面的了解，敬请参考原课程。",-1),f=c('<h2 id="models" tabindex="-1"><a class="header-anchor" href="#models" aria-hidden="true">#</a> Models</h2><p>构建模型可以参考两个来源</p><ul><li>有领域的专家显式的指示</li><li>从数据中发觉潜在的</li></ul><img src="https://pic.hanjiaming.com.cn/2023/01/03/846c357e7092d.png" alt="1672759356454.png" style="zoom:33%;"><p>模型的表示是显式的，声明的。</p><p>不确定性地方来源</p><ul><li>Partial knowledge of state of the world「对世界状况的部分了解，而不是全面的」</li><li>Noisy observations</li><li>Phenomena not covered by our model</li><li>Inherent stochasticity「固有的随机性」</li></ul><h2 id="probability-theory" tabindex="-1"><a class="header-anchor" href="#probability-theory" aria-hidden="true">#</a> Probability Theory</h2><ul><li>具有明确语义的声明性表述</li><li>强大的推理模式</li><li>既定的学习方法</li></ul><p>Complex Systems</p><ul><li>Random variables X1,..., Xn</li><li>Joint distribution P(X1,..., Xn)</li></ul><h2 id="graphical-models" tabindex="-1"><a class="header-anchor" href="#graphical-models" aria-hidden="true">#</a> Graphical Models</h2><p>两种常见的图形化表示模型</p><figure><img src="https://pic.hanjiaming.com.cn/2023/01/03/dcfd0ab39b812.png" alt="1672759835044.png" tabindex="0" loading="lazy"><figcaption>1672759835044.png</figcaption></figure><figure><img src="https://pic.hanjiaming.com.cn/2023/01/03/29f3ddeb0c017.png" alt="1672759865840.png" tabindex="0" loading="lazy"><figcaption>1672759865840.png</figcaption></figure><h2 id="applications" tabindex="-1"><a class="header-anchor" href="#applications" aria-hidden="true">#</a> Applications</h2><ul><li>Medical diagnosis</li><li>Fault diagnosis「故障诊断」</li><li>Natural language processing</li><li>Traffic analysis</li><li>Social network models「社交网络模型」</li><li>Message decoding</li><li>Computer vision <ul><li>Image segmentation</li><li>3D reconstruction</li><li>Holistic scene analysis「整体场景分析」</li></ul></li><li>Speech recognition</li><li>Robot localization &amp; mapping「机器人定位和测绘」</li></ul>',17);function _(y,x){const a=t("ExternalLinkIcon");return n(),r("div",null,[p,i("ul",null,[i("li",null,[e("这一部分内容来自斯坦福大学的公开课："),i("a",h,[e("https://www.coursera.org/learn/probabilistic-graphical-models"),l(a)])]),i("li",null,[e("参考书："),i("a",u,[e("https://mitpress.mit.edu/books/probabilistic-graphical-models"),l(a)])])]),g,m,b,s(" more "),f])}const v=o(d,[["render",_],["__file","introduction.html.vue"]]);export{v as default};
